import React from 'react'
import { useDispatch, /*useSelector*/ } from 'react-redux'
// import { useEffect } from 'react'
import { switchLanguage } from '../../actions/index'
import './LanguageSwitch.css'

export default function LanguageSwitch() {
    const dispatch = useDispatch()
    // const language = useSelector((state) => state.language)

    // useEffect(() => {
    //     dispatch(switchLanguage())
    // }
    // , [dispatch])

    function handleClick(e) {
        e.preventDefault()
        dispatch(switchLanguage())
    }

  return (
    <div className="switch-button">
        <input type="checkbox" name="switch-button" id="switch-label" className="switch-button__checkbox" onClick={(e) => handleClick(e)}/>

            <label for="switch-label" className="switch-button__label"></label>
    </div>
  )
}
