import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { useEffect, /*useState*/ } from 'react'
import { switchLanguage } from '../../actions/index'
// import './LanguageSwitch.css'

export default function LanguageSwitch() {
    const dispatch = useDispatch()
    // const language = useSelector((state) => state.language)
    // const [language, setLanguage] = useState('en')

    useEffect(() => {
        dispatch(switchLanguage())
    }
    , [dispatch])

    // let options = "es";

    function handleClick(e) {
      console.log(e.target.value);
      // if (options === "es") {
      //   options = "en";
      // } else {
      //   options = "es";
      // };
        e.preventDefault()
        dispatch(switchLanguage(e.target.value))
    }

  return (
    <div className="switch-button">
      <button name='english' value={"en"} onClick={(e) => handleClick(e)}>EN</button>
      <button name='spanish' value={"es"} onClick={(e) => handleClick(e)}>ES</button>
        {/* <input type="checkbox" name="switch-button" id="switch-label" className="switch-button__checkbox" onClick={(e) => handleClick(e)}/>

            <label for="switch-label" className="switch-button__label"></label> */}
    </div>
  )
}
