import {
    GET_COUNTRIES,
    GET_COUNTRIES_BY_NAME,
    GET_COUNTRY_BY_ID,
    ORDER_BY_NAME,
    POST_ACTIVITIES,
} from "../actions/index";

const initialState = {
    countries: [],
    country: {},
    countryDetails: {},
    activities: [],
};

function rootReducer(state = initialState, action) {
    switch (action.type) {
        case GET_COUNTRIES:
            return {
                ...state,
                countries: action.payload,
            };
        case GET_COUNTRIES_BY_NAME:
            return {
                ...state,
                // country: action.payload,
                countries: action.payload,
            };
        case GET_COUNTRY_BY_ID:
            return {
                ...state,
                countryDetails: action.payload,
            };
        case ORDER_BY_NAME:
                let orderedCountries = action.payload === 'asc' ? state.countries.sort(function (a, b) {
                    if (a.name > b.name) {
                        return 1;
                    }
                    if (b.name > a.name) {
                        return -1;
                    }
                    return 0;
                }) :
                    state.countries.sort(function (a, b) {
                        if (a.name > b.name) {
                            return -1;
                        }
                        if (b.name > a.name) {
                            return 1;
                        }
                        return 0;
                    });
                return {
                    ...state,
                    countries: orderedCountries,
                };
        case POST_ACTIVITIES:
            return {
                ...state,
                activities: action.payload,
            };
        default:
            return state;
    }
}

export default rootReducer;
