import React from "react";
import { NavLink } from "react-router-dom";
import "./Card.css";

export default function Card({id, flag, name, continent}) {

  function continentName (continent){
  switch (continent) {
    case "Africa":
      return "África";
    case "Asia":
      return "Asia";
    case "Europe":
      return "Europa";
    case "NorthAmerica":
      return "Norteamérica";
    case "Oceania":
      return "Oceanía";
    case "SouthAmerica":
      return "Sudamérica";
    default:
      break;
  }
}


  return (
    <div className="card">
      <h1 className="country">{name}</h1>
      <h2 className="continent">{continent}</h2>
      <NavLink to={`/countries/${id}`}>
        <img className="flag" src={flag} alt={name} />
      </NavLink>

    </div>
  );
}
