import React from 'react'
import { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { NavLink, useNavigate} from 'react-router-dom';
import { getCountriesNames, postActivities } from '../../actions/index';



function validate(input){
  let error = {};
  if(!input.name){
    error.name = "Activity name is required";
  }
  if(!input.duration){
    error.duration = "The duration of the activity is required";
    }
  return error;
};


export default function ActivityCreationForm() {

  const dispatch = useDispatch();

  const navigate = useNavigate();

  const countriesNames = useSelector((state) => state.countriesNames);

  const [error, setError] = useState({
    name: "Activity name is required",
    duration: "",
  });

  const [input, setInput] = useState({
    name: "",
    difficulty: "",
    duration: "",
    season: "",
    countriesList: [],
  });

  useEffect(() => {
    dispatch(getCountriesNames());
  }, []);

  function handleOnChange(e) {
    setInput({
      ...input,
      [e.target.name]: e.target.value,
    })
    setError(validate({
      ...input,
      [e.target.name]: e.target.value,
    }))
  };

  function handleSeason(e) {
    setInput({
      ...input,
      season: e.target.value,
    })
    setError(validate({
      ...input,
      [e.target.name]: e.target.value,
    }))
  };

  function handleCountry(e) {
    setInput({
      ...input,
      countriesList: [...input.countriesList, e.target.value],
    })
    setError(validate({
      ...input,
      [e.target.name]: e.target.value,
    }))
  }

  function handleDelete(e) {
    setInput({
      ...input,
      countriesList: input.countriesList.filter((c) => c !== e.target.value),
    })
    // setError(validate({
    //   ...input,
    //   [e.target.name]: e.target.value,
    // }))
  }

  function handleSubmit(e) {
    e.preventDefault();
    dispatch(postActivities(input));
    setInput({
      name: "",
      difficulty: "",
      duration: "",
      season: "",
      countriesList: [],
    })
    alert("Activity created!");
    navigate('/countries');
  }


  return (
    <div>ActivityCreationForm</div>
  )
}
